import marked from 'marked';
import 'components/red-error';
import ArticleMeta from './ArticleMeta';
import CommentContainer from './CommentContainer';
import { Component, unsafeHTML, html } from 'components/base';

class ArticlePage extends Component {
  constructor(...args) {
    super(...args);

    this.handleDeleteArticle = slug => {
      this.deleting = true;
      this.context.stores.articlesStore.deleteArticle(slug).then(() => this.$router.replaceWith('home')).finally(() => this.deleting = false);
    };

    this.handleDeleteComment = id => {
      this.context.stores.commentsStore.deleteComment(id);
    };
  }

  set $route(value) {
    this.slug = value.params.id;
  }

  connectedCallback() {
    super.connectedCallback();
    const slug = this.slug;
    this.context.stores.articlesStore.loadArticle(slug, {
      acceptCached: true
    });
    this.context.stores.commentsStore.setArticleSlug(slug);
    this.context.stores.commentsStore.loadComments();
  }

  render() {
    const slug = this.slug;
    const {
      currentUser
    } = this.context.stores.userStore;
    const {
      comments,
      commentErrors
    } = this.context.stores.commentsStore;
    const article = this.context.stores.articlesStore.getArticle(slug);

    if (this.deleting) {
      return html`
        <loading-spinner></loading-spinner>
      `;
    }

    if (!article) return html`
        <red-error message="Can't load article"></red-error>
      `;
    const markup = marked(article.body, {
      sanitize: true
    });
    const canModify = currentUser && currentUser.username === article.author.username;
    return html`
      <div class="article-page">
        <div class="banner">
          <div class="container">
            <h1>${article.title}</h1>
            ${ArticleMeta({
      article,
      canModify,
      onDelete: this.handleDeleteArticle
    })}
          </div>
        </div>
        <div class="container page">
          <div class="row article-content">
            <div class="col-xs-12">
              ${unsafeHTML(markup)}

              <ul class="tag-list">
                ${article.tagList.map(tag => {
      return html`
                    <li class="tag-default tag-pill tag-outline">
                      ${tag}
                    </li>
                  `;
    })}
              </ul>
            </div>
          </div>
          <hr />
          <div class="article-actions"></div>
          <div class="row">
            ${CommentContainer({
      comments,
      errors: commentErrors,
      slug,
      currentUser,
      onDelete: this.handleDeleteComment
    })}
          </div>
        </div>
      </div>
    `;
  }

}

ArticlePage.observedContexts = ['stores'];
ArticlePage.properties = {
  slug: {
    type: String
  }
};
customElements.define('article-page', ArticlePage);

import ArticleActions from './ArticleActions';
import { html } from 'lit-html';

const ArticleMeta = ({
  article,
  canModify,
  onDelete
}) => {
  return html`
    <div class="article-meta">
      <a href=${`#@${article.author.username}`}
        ><img src=${article.author.image} alt=""
      /></a>
      <div class="info">
        <a href=${`#@${article.author.username}`} class="author"
          >${article.author.username}</a
        ><span class="date">${new Date(article.createdAt).toDateString()}</span>
      </div>
      ${ArticleActions({
    article,
    canModify,
    onDelete
  })}
    </div>
  `;
};

export default ArticleMeta;

import './comment-input';
import CommentList from './CommentList';
import 'components/list-errors';
import { html } from 'lit-html';

const CommentContainer = props => {
  if (props.currentUser) {
    return html`
      <div class="col-xs-12 col-md-8 offset-md-2">
        <div>
          <list-errors .errors=${props.errors}></list-errors>
          <comment-input
            .currentUser=${props.currentUser}
