import { html } from 'lit-html';

const ArticleActions = props => {
  const article = props.article;

  const handleDelete = () => props.onDelete(article.slug);

  if (props.canModify) {
    return html`
      <span
        ><a
          href=${`#editor/${article.slug}`}
