                ${FollowUserButton({
      isUser,
      username: profile.username,
      following: profile.following,
      unfollow: this.handleUnfollow,
      follow: this.handleFollow
    })}
              </div>
            </div>
          </div>
        </div>
        <div class="container">
          <div class="row">
            <div class="col-xs-12 col-md-10 offset-md-1">
