export default ArticleActions;

import { Component, html, ifDefined } from 'components/base';

class CommentInput extends Component {
  constructor() {
    super();
    this.body = '';

    this.handleBodyChange = ev => {
      this.body = ev.target.value;
    };

    this.createComment = ev => {
      ev.preventDefault();
      this.context.stores.commentsStore.createComment({
        body: this.body
      }).then(() => this.body = '');
    };
  }

  render() {
    const {
      isCreatingComment
    } = this.context.stores.commentsStore;
    return html`
      <form class="card comment-form" @submit=${this.createComment}>
        <div class="card-block">
          <textarea
            class="form-control"
            placeholder="Write a comment..."
            .value=${this.body}
            ?disabled=${isCreatingComment}
            @change=${this.handleBodyChange}
            rows="3"
          ></textarea>
        </div>
        <div class="card-footer">
          <img
            src=${ifDefined(this.currentUser.image || undefined)}
            class="comment-author-img"
            alt=""
          /><button class="btn btn-sm btn-primary" type="submit">
            Post Comment
          </button>
        </div>
      </form>
    `;
  }

}

CommentInput.observedContexts = ['stores'];
CommentInput.properties = {
  body: {
    type: String
  },
  currentUser: {}
};
customElements.define('comment-input', CommentInput);

import Comment from './Comment';
import { html } from 'lit-html';
import { repeat } from 'components/base';

const CommentList = props => {
  const {
    currentUser,
    onDelete,
    slug
  } = props;
  return html`
    <div>
      ${repeat(props.comments, comment => comment.id, comment => Comment({
    comment,
    currentUser,
    onDelete,
    slug
  }))}
    </div>
  `;
};

export default CommentList;

import DeleteButton from './DeleteButton';
import { html } from 'lit-html';
import { ifDefined } from 'components/base';

const Comment = props => {
  const comment = props.comment;
  const show = props.currentUser && props.currentUser.username === comment.author.username;
  return html`
    <div class="card">
      <div class="card-block"><p class="card-text">${comment.body}</p></div>
      <div class="card-footer">
        <a url=${`/@${comment.author.username}`} class="comment-author"
          ><img
            src=${ifDefined(comment.author.image || undefined)}
            class="comment-author-img"
            alt=""
        /></a>
        <a url=${`/@${comment.author.username}`} class="comment-author"
          >${comment.author.username}</a
        ><span class="date-posted"
          >${new Date(comment.createdAt).toDateString()}</span
        >
        ${DeleteButton({
    show,
    slug: props.slug,
    commentId: comment.id,
    onDelete: props.onDelete
  })}
      </div>
    </div>
  `;
};

export default Comment;

import { html } from 'lit-html';

const DeleteButton = props => {
  const handleClick = () => props.onDelete(props.commentId);

  if (props.show) {
    return html`
      <span class="mod-options"
        ><i class="ion-trash-a" @click=${handleClick}></i
      ></span>
    `;
  }

  return null;
};

export default DeleteButton;

